mapper.update:
UPDATE COUPON SET 
CNOTIFY = #{cnotify},
CIMG = #{cimg},
CTHUMBIMG = #{cthumbimg},
COUPON = #{coupon}
WHERE CSEQ = #{cseq}

UPDATE COUPONDETAIL SET 
CDSTATE = #{cdstate}
WHERE CSEQ = #{cseq}

mapper.select:
SELECT CSEQ, CNOTIFY, CTHUMBIMG, CDATE FROM COUPON 
WHERE CENABLE = 'Y' 
AND SELLERID = #{sellerid}
