INSERT INTO PRODUCT (PCODE, SELLERID, ICODE, ROTNUM, PDATE, LIFETIME, STOCK, INAME, PSTATE) SELECT PRODUCT_SEQ.NEXTVAL, A.* FROM(
SELECT #{sellerid} AS SELLERID, #{item.icode} AS ICODE, (SELECT LCODE FROM MCATE WHERE MCODE = (SELECT MCODE FROM INVENTORY WHERE ICODE = #{item.icode}))||(SELECT MCODE FROM INVENTORY WHERE ICODE = #{item.icode})||#{item.icode} AS ROTNUM, SYSDATE AS PDATE, SYSDATE+7 AS LIFETIME, #{item.stock} AS STOCK, (SELECT INAME FROM INVENTORY WHERE ICODE = #{item.icode}) AS INAME, 'C' AS PSTATE FROM DUAL
) A

